mapper.select:

select b_num, b_title, b_content, b_writer, regdate, updatedate, id 
from (
  select /*+INDEX_DESC(tbl_qna pk_board) */
  rownum rn, b_num, b_title, b_content, b_writer, regdate, updatedate, id 
  from tbl_qna 
  where rownum <= #{pageNum} * #{amount}
  and id = #{id, jdbcType=VARCHAR}
)
where rn > (#{pageNum} - 1) * #{amount}
